const Icon: React.FC<IconProps> = ({ name, className, ...props }) => {
  const iconData = iconsData[name];
  if (!iconData) {
    console.error(`Ícono con el nombre "${name}" no encontrado.`);
    return null;
  }

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox={iconData.viewBox}
      fill="currentColor" // Permite cambiar el color con CSS
      className={className}
      {...props}
    >
      {iconData.path}
    </svg>
  );
};

export default Icon;